<?phpclass VES_VendorsContact_Block_Vendor_Notification extends Mage_Core_Block_Template{	protected function _getVendorSession(){		return Mage::getSingleton('vendors/session');	}	public function getUpgradeUrl(){		return $this->getUrl("vendors/config/index",array("section"=>"vendorscontact"));	}    public function _toHtml(){        $email =  Mage::helper('vendorsconfig')->getVendorConfig('vendorscontact/contact/email',$this->_getVendorSession()->getId());        if($email) return '';        return parent::_toHtml();    }}